/**
* Page Duplication Flow Function
* 
* This function handles the duplication of a Directus page, including all its content blocks.
* It allows specifying a new title and permalink, or automatically creates them with "-Copy" suffix.
* The duplicated page is always set to draft status for review before publishing.
* 
* @param {Object} data - Data object from Directus Flow
* @param {Object} data.$trigger.body - Optional data passed to the flow (new title/permalink)
* @param {Object} data.item - The original page being duplicated with all its properties
* @returns {Object} Modified page data for creating the duplicate
*/
module.exports = function(data) {
   // Get the data submitted with the flow trigger (if any)
   const triggerData = data.$trigger.body;

   // Get the full data of the existing page to duplicate
   const existingData = data.item;

   // Set the title for the duplicate:
   // - Use the provided title if it exists in the trigger data
   // - Otherwise, append "- Copy" to the original page title
   existingData.title = triggerData.title ?? existingData.title + ' - Copy';

   // Set the permalink for the duplicate:
   // - Use the provided permalink if it exists in the trigger data
   // - Otherwise, append "-copy" to the original permalink
   existingData.permalink = triggerData.permalink ?? existingData.permalink + '-copy';

   // Always set the duplicate page to draft status
   // This prevents accidental publishing of duplicate content
   existingData.status = 'draft';

   // Return the modified data to create the duplicate page
   // Note: Directus will handle copying all related blocks/components
   return existingData;
}

/**
 * Automatic Redirect Generator for Content URL Changes
 * 
 * This function generates redirect objects when published content URLs change.
 * It works with both posts and pages collections to maintain SEO value
 * and prevent broken links when content is moved or renamed.
 */
const map = {
  // Configuration for blog posts
  posts: {
    slugField: "slug",        // Field that contains the URL identifier for posts
    routePrefix: "/blog/",    // Prefix added to all post URLs (e.g., /blog/my-post)
  },
  // Configuration for pages
  pages: {
    slugField: "permalink",   // Field that contains the URL identifier for pages
    routePrefix: "",          // No prefix for pages (e.g., /about-us)
  },
};

/**
 * Flow function executed when content URL changes
 * @param {Object} data - Data object from Directus Flow
 * @param {Object} data.$trigger - Information about what triggered the flow
 * @param {string} data.$trigger.collection - Collection name (posts/pages)
 * @param {Object} data.$trigger.payload - New data being saved
 * @param {Object} data.item - Previous version of the item before changes
 * @returns {Object} Redirect configuration object
 */
module.exports = function (data) {
  // Get the collection configuration based on whether this is a page or post
  const collection = map[data.$trigger.collection];

  const oldSlug = data.item[collection.slugField];
  const newSlug = data.$trigger.payload[collection.slugField];

  // Build the old URL by combining the route prefix with the previous slug/permalink
  const oldUrl = `${collection.routePrefix}${oldSlug}`;

  // Build the new URL by combining the route prefix with the new slug/permalink
  const newUrl = `${collection.routePrefix}${newSlug}`;

  // Add a note
  const note = `Created by Redirect automation on ${new Date().toLocaleDateString("en-US")} at ${new Date().toLocaleTimeString("en-US")} when a ${data.$trigger.collection} ${collection.slugField} was updated from "${oldSlug}" to "${newSlug}"`

  // Return a redirect object that will be saved to the redirects collection
  return {
    response_code: 301,       // Permanent redirect (best for SEO)
    url_from: oldUrl,         // Where users were going before
    url_to: newUrl,           // Where they should be sent now
    note,
  };
};

/**
 * Prepares email template data by matching trigger values with form fields
 * @param {Object} trigger - The trigger object containing payload values
 * @param {Object} form - The form object containing fields and email templates
 * @returns {Array} Array of prepared email template objects
 * @throws {Error} If required data is missing or invalid
 */
function prepareEmailTemplateData(trigger, form) {
  // Validate input parameters
  if (!trigger?.payload?.values || !Array.isArray(trigger.payload.values)) {
    throw new Error('Invalid trigger payload values');
  }

  if (!form?.fields || !Array.isArray(form.fields)) {
    throw new Error('Invalid form fields');
  }

  if (!form?.emails || !Array.isArray(form.emails)) {
    throw new Error('Invalid form emails');
  }

  // Create an object to store the field name-value pairs
  const data = {};

  // Iterate through the trigger values and match them with form fields
  trigger.payload.values.forEach(item => {
    if (!item || typeof item !== 'object') {
      throw new Error('Invalid trigger value item');
    }

    // Ensure required properties exist
    if (!item.field && !item.field_name) {
      throw new Error('Missing field identifier in trigger value');
    }

    if (item.value === undefined) {
      throw new Error('Missing value in trigger value');
    }

    // Find the corresponding field in the form
    const formField = form.fields.find(field => field.id === item.field);

    // If a matching field is found, use its name as the key
    if (formField && formField.name) {
      data[formField.name] = item.value;
    } else {
      // Fallback to using the field_name from the trigger if no match is found
      data[item.field_name || item.field] = item.value;
    }
  });

  // Process and validate all email templates
  const emailTemplates = form.emails.map(email => {
    // Validate required email template properties
    if (!email.subject) {
      throw new Error('Missing email subject');
    }

    if (!email.message) {
      throw new Error('Missing email message template');
    }

    if (!email.to) {
      throw new Error('Missing email recipient');
    }

    // Normalize 'to' field to always be an array
    const toField = Array.isArray(email.to) ? email.to : [email.to];

    // Validate each email address
    toField.forEach(recipient => {
      if (typeof recipient !== 'string' || !recipient.trim()) {
        throw new Error('Invalid email recipient');
      }
    });

    return {
      to: toField,
      subject: email.subject,
      template: email.message,
      data: data
    };
  });

  return emailTemplates;
}

module.exports = function(data) {
  if (!data?.$trigger || !data?.form) {
    throw new Error('Missing required data');
  }

  return prepareEmailTemplateData(data.$trigger, data.form);
};
